CREATE TABLE ADDRESS (AID VARCHAR(20), STREET VARCHAR(20), CITY VARCHAR(20), POSTALCODE VARCHAR(20), PHONENUMBER VARCHAR(15), PRIMARY KEY (AID))
CREATE TABLE SERVCES (SID VARCHAR(5), DINE_IN INTEGER, TAKE_OUT INTEGER, BYOD INTEGER, DELIVERY INTEGER, PRIMARY KEY (SID))
CREATE TABLE RESTAURANTS (RID VARCHAR(5), AID VARCHAR(5), SID VARCHAR(5), RESTNAME VARCHAR(20), PRICEBRACKET VARCHAR(5), HOURS VARCHAR(5), WEBSITE VARCHAR(20), PRIMARY KEY (RID), FOREIGN KEY AID REFERENCES ADDRESS (AID), FOREIGN KEY SID REFERENCES SERVICES (SID))
CREATE TABLE CUSTOMERS (CID VARCHAR(5), RID VARCHAR(5), FIRSTNAME VARCHAR(20), LASTNAME VARCHAR(20), RESTAURANTHISTORY (???), NUMBEROFREVIEWS INTEGER, EMAIL VARCHAR(20), PRIMARY KEY (CID),  FOREIGN KEY RID REFERENCES CUSTOMERS (CID))
CREATE TABLE CRITIC (CID VARCHAR(5), REPUTATION, FOREIGN KEY CID REFERENCES CUSTOMER (CID))
CREATE TABLE REVIEW (REVIEWID VARCHAR(5), CID VARCHAR(5), RID VARCHAR(5), COMMENTS VARCHAR(50), RATINGS INTEGER, DATES DATE, PRIMARY KEY (REVIEWID),  FOREIGN KEY CID REFERENCES CUSTOMERS (CID), FOREIGN KEY RID REFERENCES RESTAURANTS (RID))
CREATE TABLE MENU (MID VARCHAR(5), RID VARCHAR(5), CUISINE VARCHAR(20), MENU_DESCRIPTION VARCHAR(20), PRIMARY KEY (MID), FOREIGN KEY RID REFERENCES RESTAURANTS (RID))
CREATE TABLE FOODITEM (FID VARCHAR(5), MID VARCHAR(5), FOODNAME VARCHAR(20), FOODPRICE DOUBLE, FOREIGN KEY MID REFERENCES MENU (MID))